const { AHShopClient } = require('../dist/index');

const ahShop = new AHShopClient();

const filter = {
    sortBy: '-price',
    property: ['nutriscore:a', 'store_department:houdbaar']
}

ahShop.product().getProductByName("Red Bull", filter).then(result => console.log(result))

